mapper.select:
select b.keyWord 
from (
	select rownum as rs, a.* 
	from (
		select count(keyWord) as cnt, keyWord 
		from searchWordTest where keyDate like '%'||to_char(sysdate, 'YY/MM/DD')||'%' 
		group by keyWord 
		order by cnt desc ) a
) b 
where rs between 1 and 10

select c.*, d.re_cnt, nvl(c.show, 0)+nvl(c.p_love, 0)+nvl(d.re_cnt, 0) as cnt
from (
	select a.*, b.p_love, b.commCnt 
	from  (
		select * 
		from postTest 
		where cont like '%í•˜%' and id = 'nuit0204' and privacy = 1
	) a left outer join (
		select dd.*, bb.commCnt 
		from (
			select no,  count(no) as p_love 
			from p_loveTest 
			group by no
		) dd
		full outer join (
			select count(no) as commCnt, no 
			from commTest 
			group by no
		) bb
		on dd.no = bb.no
		where dd.no is not null and bb.no is not null
	) b on a.no = b.no
) c
left outer join (
	select count(re_no) as re_cnt, re_no 
	from postTest 
	group by re_no
) d
on c.no = d.re_no
order by cnt desc;
